#!/bin/sh
# Add your startup script

# DO NOT DELETE

# Dynamic flag generated by CTFd-whale

cd /home/ctf
echo $FLAG > /tmp/flag.txt
cut -c -20 /tmp/flag.txt > /tmp/flag1.txt
cut -c 21- /tmp/flag.txt > /tmp/flag2.txt
rm /tmp/flag.txt

chmod 777 /tmp/flag1.txt
chmod 777 /tmp/flag2.txt
chmod 777 /app/app.py

chown -R ctf /tmp/flag1.txt
chown -R ctf /tmp/flag2.txt
chown -R ctf app.py

export FLAG=not_flag
FLAG=not_flag

su ctf -c "flask --debug run -h 0.0.0.0 -p 8080"